import React, { useEffect, useRef } from 'react';
import styles from './Features.module.css';

const FEATURES = [
  {
    icon: 'ü§ñ', num: '01', colorClass: 'cyan', wide: false,
    title: 'Asistente IA Personalizado',
    desc: 'Analiza tu progreso y recomienda contenido adaptado a tu nivel, ritmo de aprendizaje y objetivos laborales en tiempo real.',
  },
  {
    icon: '‚ö°', num: '02', colorClass: 'lime', wide: false,
    title: 'Microlearning',
    desc: 'Lecciones cortas y concretas pensadas para aprender en cualquier momento, sin perder el hilo de tu ruta formativa.',
  },
  {
    icon: 'üó∫Ô∏è', num: '03', colorClass: 'purple', wide: true,
    title: 'Rutas de Formaci√≥n por Competencias',
    desc: 'Secuencias de aprendizaje estructuradas por habilidades pr√°cticas ‚Äî desde fundamentos hasta especializaci√≥n profesional en √°reas como backend, frontend, DevOps, IA, ciberseguridad y cloud. Cada ruta est√° alineada con la demanda laboral real del sector tech en Medell√≠n y Colombia.',
  },
  {
    icon: 'üìä', num: '04', colorClass: 'orange', wide: false,
    title: 'Dashboard de Progreso',
    desc: 'Visualiza tu avance, eval√∫a tus competencias y accede a anal√≠tica de aprendizaje que te gu√≠a hacia tus metas.',
  },
  {
    icon: 'üë•', num: '05', colorClass: 'cyan', wide: false,
    title: 'Comunidad & Colaboraci√≥n',
    desc: 'Foros, mentor√≠as y grupos de trabajo para construir red profesional dentro del ecosistema tecnol√≥gico local.',
  },
  {
    icon: 'üèÜ', num: '06', colorClass: 'lime', wide: false,
    title: 'Evaluaci√≥n Iterativa',
    desc: 'Sistema de evaluaci√≥n continua con retroalimentaci√≥n inmediata para consolidar habilidades pr√°cticas de manera efectiva.',
  },
];

function FeatureCard({ feature }) {
  return (
    <div className={`${styles.card} ${feature.wide ? styles.wide : ''}`}>
      <div className={`${styles.icon} ${styles[`fi_${feature.colorClass}`]}`}>
        {feature.icon}
      </div>
      <div className={styles.num}>{feature.num}</div>
      <div className={styles.title}>{feature.title}</div>
      <div className={styles.desc}>{feature.desc}</div>
    </div>
  );
}

export default function Features() {
  const ref = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => entries.forEach(e => {
        if (e.isIntersecting) e.target.classList.add('visible');
      }),
      { threshold: 0.08 }
    );
    ref.current?.querySelectorAll('.reveal').forEach(el => observer.observe(el));
    return () => observer.disconnect();
  }, []);

  return (
    <section className={styles.section} ref={ref}>
      <p className={`${styles.tag} reveal`}>// Funcionalidades clave</p>
      <h2 className={`${styles.title} reveal`}>
        Todo lo que necesitas<br />para aprender TI en un solo lugar
      </h2>
      <div className={`${styles.grid} reveal`}>
        {FEATURES.map((f, i) => <FeatureCard key={i} feature={f} />)}
      </div>
    </section>
  );
}
